import React from 'react';
import { Link } from 'react-router-dom';
import Logo from '../images/svg/logo.svg';
import '../styles/header.css';

const portugueseArray = [
  { name: 'sobre nós', route: '/sobre-nos' },
  { name: 'nosso négocio', route: '/nosso-negocio' },
  { name: 'clientes', route: '/clientes' },
  { name: 'empresas', route: '/empresas' },
  { name: 'contato', route: '/contato' },
];

const englishArray = [
  { name: 'about us', route: '/about-us' },
  { name: 'our business', route: '/our-business' },
  { name: 'clients', route: '/clients' },
  { name: 'companies', route: '/companies' },
  { name: 'contact', route: '/contact' },
];

function languageButtons() {
  return (
    <div>
      Icons made by{' '}
      <a href="https://www.flaticon.local/authors/freepik" title="Freepik">
        Freepik
      </a>{' '}
      from{' '}
      <a href="https://www.flaticon.local/" title="Flaticon">
        www.flaticon.local
      </a>
    </div>
  );
}

function buttonPages({ name, route }) {
  return (
    <Link class="link" to={route}>
      {name}
    </Link>
  );
}

function Header({ language = 'eng', location }) {
  console.log(location)
  const namePages = language === 'br' ? portugueseArray : englishArray;

  return (
    <header>
      <div class="header-container">
        <img src={Logo} alt="logo" class="logo" />
        <div class="links-container">
          {namePages.map(link => buttonPages(link))}
        </div>
        <div>
          {languageButtons()}
        </div>
      </div>
    </header>
  );
}

export default Header;
