import React from 'react';
import Header from '../components/Header';
import '../styles/aboutus.css';
import IMG1 from '../images/svg/img_test.svg';
import IMG2 from '../images/svg/img_test_2.svg';

const portugueseTexts = {
  title: 'Lorem ipsum dolor sit amet',
  subtitle: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
};

const englishTexts = {
  title: 'Lorem ipsum dolor sit amet',
  subtitle: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
};

function titleInfo(title) {
  return (
    <div>
      <p>{title}</p>
      <div>border</div>
    </div>
  );
}

function subtitleInfo(subtitle) {
  return <div>{subtitle}</div>;
}

function cardsInfo() {
  const arrayOfImages = [
    [IMG1, 'Lorem Ipsum'],
    [IMG1, 'Lorem Ipsum'],
    [IMG1, 'Lorem Ipsum'],
    [IMG1, 'Lorem Ipsum'],
  ];
  return arrayOfImages.map(arr => (
    <div>
      <p>{arr[1]}</p>
      <img src={arr[0]} />
    </div>
  ));
}

function infoContainer({ title, subtitle }) {
  return (
    <div>
      {titleInfo(title)}
      {subtitleInfo(subtitle)}
      {cardsInfo()}
    </div>
  );
}

function imgContainer() {
  return (
    <div>
      <img src={IMG2} />
    </div>
  );
}

function pageContainer(obj) {
  return (
    <div className="about-us-page-container">
      {infoContainer(obj)}
      {imgContainer()}
    </div>
  );
}

function AboutUs() {
  const {
    location: { pathname },
  } = window;
  const namePages = pathname.includes('/en') ? englishTexts : portugueseTexts;

  return (
    <div>
      <Header />
      {pageContainer(namePages)}
    </div>
  );
}

export default AboutUs;
