import React from 'react';
import Header from '../components/Header';
import '../styles/ourClients.css';
import Retangle from '../images/svg/retangle.svg';
import ClientCard from '../images/svg/clients_logo.svg';
import AliansceSonae from '../images/svg/aliansce_sonae.svg';
import AngaAsset from '../images/svg/anga_asset.svg';
import AppProva from '../images/svg/logodois.svg';
import Asics from '../images/svg/Logo (1).svg';
import Avianca from '../images/svg/Logo (2).svg';
import Babolat from '../images/svg/Logo (3).svg';
import BB from '../images/svg/Logo (4).svg';
import Barracuda from '../images/svg/Logo (5).svg';
import BMG from '../images/svg/Logo (6).svg';
import Ecosolys from '../images/svg/Logo (7).svg';
import CLaro from '../images/svg/Logo (9).svg';
import SLconstrutora from '../images/svg/Logo (8).svg';
import Correios from '../images/svg/Logo (10).svg';
import Csul from '../images/svg/csul.svg';
import Daylong from '../images/svg/Logo (11).svg';
import Deloitte from '../images/svg/Logo (13).svg';
import Estacio from '../images/svg/Logo (12).svg';
import Faberg from '../images/svg/Logo (14).svg';

const englishText = {
  title: 'Lorem ipsum consectetur.',
  subtitle: 'Nam ac arcu dignissim, posuere mi id,',
  h1Name: 'Our clients',
  clients: [
    { img: AliansceSonae, title: 'Aliansce sonae', subtitle: 'Administradora de shoppings centers' },
    { img: AngaAsset, title: 'Angá asset', subtitle: 'Gestão de fundos de investimentos' },
    { img: AppProva, title: 'AppProva', subtitle: 'Plataforma de diagnóstico acadêmico ' },
    { img: Asics, title: 'Asics', subtitle: 'Empresa de artigos desportivos' },
    { img: Avianca, title: 'Avianca airlines', subtitle: 'Companhia aérea brasileira' },
    { img: Babolat, title: 'Babolat', subtitle: 'Empresa de equipamento esportivos' },
    { img: BB, title: 'Banco do Brasil', subtitle: 'instituição financeira brasileira' },
    { img: Barracuda, title: 'Barracuda', subtitle: 'Empresa de segurança, rede e armazenamento ' },
    { img: BMG, title: 'Banco BMG', subtitle: 'Instituição financeira do Brasil' },
    { img: Ecosolys, title: 'Ecosolys', subtitle: 'Fabricante de produtos para energia solar' },
    { img: CLaro, title: 'Claro', subtitle: 'Serviço de telefonia' },
    { img: SLconstrutora, title: 'SL Construtora', subtitle: 'Empresa de construção civil' },
    { img: Correios, title: 'Correios', subtitle: 'Empresa de entrega de correspondências' },
    { img: Csul, title: 'Csul', subtitle: 'Projeto de desenvolvimento urbano' },
    { img: Daylong, title: 'Daylong', subtitle: 'Fabricante de produtos dermocosméticos' },
    { img: Deloitte, title: 'Deloitte', subtitle: 'Consultoria de gerenciamento' },
    { img: Estacio, title: 'Estácio', subtitle: 'Universidade privada brasileira ' },
    { img: Faberg, title: 'Faberg', subtitle: 'Empresa de eventos esportivos' },
  ],
};

const portugueseText = {
  title: 'Lorem ipsum consectetur.',
  subtitle: 'Nam ac arcu dignissim, posuere mi id,',
  h1Name: 'Nossos clientes',
};

const card =  (img, title, subtitle) =>  (
  <div className="our-client-card-box">
    <img src={img} alt="client logo" />
    <div className="our-clients-card-texts-container">
      <p className="our-clients-card-texts-title">{title}</p>
      <p className="our-clients-card-texts-subtitle">{subtitle}</p>
    </div>
  </div>
);

function clientsContainer (h1Name) {
  return  (
    <div>
      <p className="our-clients-title">{h1Name}</p>
      <div className="our-clients-cards-container">
        {card (ClientCard, 'Lorem ipsum', 'Lorem ipsum tema for gertmun')}
        {card (ClientCard, 'Lorem ipsum', 'Lorem ipsum tema for gertmun')}
        {card (ClientCard, 'Lorem ipsum', 'Lorem ipsum tema for gertmun')}
        {card (ClientCard, 'Lorem ipsum', 'Lorem ipsum tema for gertmun')}
        {card (ClientCard, 'Lorem ipsum', 'Lorem ipsum tema for gertmun')}
        {card (ClientCard, 'Lorem ipsum', 'Lorem ipsum tema for gertmun')}
        {card (ClientCard, 'Lorem ipsum', 'Lorem ipsum tema for gertmun')}
        {card (ClientCard, 'Lorem ipsum', 'Lorem ipsum tema for gertmun')}
        {card (ClientCard, 'Lorem ipsum', 'Lorem ipsum tema for gertmun')}
        {card (ClientCard, 'Lorem ipsum', 'Lorem ipsum tema for gertmun')}
      </div>
    </div>
  );
}

function headerTexts (title, subtitle) {
  return  (
    <div className="our-clients-header-texts">
      <p className="our-clients-header-title">{title}</p>
      <p className="our-clients-header-subtitle">{subtitle}</p>
      <img className="our-clients-header-img" src={Retangle} alt="principal" />
    </div>
  );
}

function ourClientsContainer ({ title, subtitle, h1Name }) {
  return  (
    <div className="our-clients-container">
      {headerTexts (title, subtitle)}
      {clientsContainer (h1Name)}
    </div>
  );
}

function OurClients () {
  const {
    location: { pathname },
  } = window;
  const namePages = pathname.includes ('/en') ? englishText : portugueseText;
  return  (
    <div>
      <Header />
      {ourClientsContainer (namePages)}
    </div>
  );
}

export default OurClients;
