import React, { useEffect } from 'react';
import CarouselImg from '../images/svg/carousel_big_img.svg';
import ArrowRight from '../images/svg/arrow_rigth.svg';
import ArrowLeft from '../images/svg/arrow_left.svg';
var slideIndex = 1;

function plusSlides(n) {
  showSlides((slideIndex += n));
}

function currentSlide(n) {
  showSlides((slideIndex = n));
}

function showSlides(n) {
  var i;
  var slides = document.getElementsByClassName('mySlides');
  var dots = document.getElementsByClassName('dot');
  if (n > slides.length) {
    slideIndex = 1;
  }
  if (n < 1) {
    slideIndex = slides.length;
  }
  for (i = 0; i < slides.length; i++) {
    slides[i].style.display = 'none';
  }
  for (i = 0; i < dots.length; i++) {
    dots[i].className = dots[i].className.replace(' active', '');
  }
  slides[slideIndex - 1].style.display = 'block';
  dots[slideIndex - 1].className += ' active';
}

function HomeCarousel() {
  useEffect(() => {
    showSlides(slideIndex);
  });
  return (
    <div class="carousel-home-container">
      <div class="slideshow-container">
        <div class="mySlides fade">
          <div className="carousel-home-box">
            <div className="carousel-img-buttons-container">
              <div className="carousel-img-big-box">
                <img src={CarouselImg} alt="idol" />
              </div>
              <div>
                <button
                  className="btn-carousel-home"
                  onClick={() => plusSlides(-1)}>
                  <img src={ArrowLeft} alt="arrow left" />
                </button>
                <button
                  className="btn-carousel-home"
                  onClick={() => plusSlides(1)}>
                  <img src={ArrowRight} alt="arrow right" />
                </button>
              </div>
            </div>
            <div>
              <p>Lorem ipsum dolor sit amet</p>
              <p>
                {' '}
                There are many variations of passages of Lorem Ipsum available,
                but the majority have suffered alteration in some form, by
                injected humour, or randomised words which don't look even
                slightly believable. If you are going to use a passage of Lorem
                Ipsum, you need to be
              </p>
              <button>VEJA MAIS</button>
            </div>
          </div>
        </div>
        <div class="mySlides fade">
          <div>
            <div>
              <img src={CarouselImg} alt="idol" />
              <button class="prev" onClick={() => plusSlides(-1)}>
                &#10094;
              </button>
              <button class="next" onClick={() => plusSlides(1)}>
                &#10095;
              </button>
            </div>
            <div>
              <p></p>
              <p></p>
              <button></button>
            </div>
          </div>
        </div>
      </div>
      <div>
        <span class="dot" onClick={() => currentSlide(1)}></span>
        <span class="dot" onClick={() => currentSlide(2)}></span>
      </div>
    </div>
  );
}

export default HomeCarousel;
